// On récupères les versions des applis
$phpVersion = phpversion();
$apacheVersion = apache_get_version();

$link = @ mysql_connect($mysqlServer, $mysqlUser, $mysqlPass);
if (!$link) {
	die('Unable to connect to your Mysql Server, please change credentials in index.php file');
} else {
	$mysqlVersion = mysql_get_server_info();
	mysql_close($link);
}

if (!is_file($xamppVersion))
	die('Unable to open Xampp Server\'s version file, please change path in index.php file');
$fp = fopen($xamppVersion, 'r');
$xamppVersionContents = fread($fp, filesize($xamppVersion));
fclose($fp);
$xamppserverVersion = $xamppVersionContents;

// Recuperation des projets
$handle = opendir(".");
$projectContents = '';
while ($file = readdir($handle)) {
	if (is_dir($file) && !in_array($file, $projectsListIgnore)) {
		// Si c'est un projet Symfony2, on rajoute /web/ dans l'adresse
		$subdir = is_dir($file . '/web') ? '/web' : '';

		$favicon_png = $file . $subdir . '/favicon.png';
		$favicon_ico = $file . $subdir . '/favicon.ico';
		$favicon_url = is_file($favicon_png) ? $favicon_png : (is_file($favicon_ico) ? $favicon_ico : '');

		$favicon = ($favicon_url != '') ? '<img class="projet-favicon" src="' . $favicon_url . '"/>' : '';

		$url_img = $file . '/screenshot.jpg';
		$url_img = (is_file($url_img)) ? $url_img : 'index.php?img=pngEmptyFolder';
		$projectContents .= '<li class="projet"><a href="' . $file . '">' .
		$favicon .
		'<img class="projet-screen" src="' . $url_img . '"/>' .
		'<span>' . $file . '</span>' .
		'</a></li>';
	}
}
closedir($handle);
if (!isset ($projectContents))
	$projectContents = $langues[$langue]['txtNoProjet'];

if ($admin && $page == 'admin_home') {
	// Recuperation des alias
	$aliasContents = '';
	if (is_dir($aliasDir)) {
		$handle = opendir($aliasDir);
		while ($file = readdir($handle)) {
			if (is_file($aliasDir . $file) && strstr($file, '.conf')) {
				$msg = '';
				$aliasContents .= '<li><a href="' . str_replace('.conf', '', $file) . '/">' . str_replace('.conf', '', $file) . '</a></li>';
			}
		}
		closedir($handle);
	}
	if (!isset ($aliasContents))
		$aliasContents = $langues[$langue]['txtNoAlias'];

	// Recuperation des extensions PHP
	$phpExtContents = '';
	$loaded_extensions = get_loaded_extensions();
	foreach ($loaded_extensions as $extension)
		$phpExtContents .= "<li>${extension}</li>";

	// Récupération des status des différents services
	$i = 0;
	$up = "";

	if ($_SERVER['SERVER_PORT'] == 443) {
		$prot = "https";
	} else {
		$prot = "http";
	}

	if (isset ($_SERVER['PHP_AUTH_USER'])) {
		$up = "$_SERVER[PHP_AUTH_USER]:$_SERVER[PHP_AUTH_PW]@";
	}
	if ('localhost' == strtolower($_SERVER['SERVER_NAME'])) { // Fix by Wiedmann
		$server = '127.0.0.1';
	} else {
		$server = $_SERVER['SERVER_NAME'];
	}
	$b = "$prot://$up" . $server;
	if ($_SERVER['SERVER_PORT'] != 80) {
		$b = "$prot://$up" . $server . ":" . $_SERVER['SERVER_PORT']; // Fix by Wiedmann
	}

	$a = @ file("$b/xampp/php.php");
	$phpStatus = $a[0];
	$a = @ file("$b/xampp/mysql.php");
	$mysqlStatus = $a[0];
	$a = @ file("$b/cgi-bin/cgi.cgi");
	$cgiStatus = $a[0];
	$a = @ file("$b/xampp/ssi.shtml");
	$ssiStatus = $a[0];

	$host = "127.0.0.1";
	$timeout = 1;

	if (($handle = @ fsockopen($host, 443, $errno, $errstr, $timeout)) == false) {
		$sslStatus = "NOK";
	} else {
		$sslStatus = "OK";
	}
	@ fclose($handle);

	if ((file_exists("\python\\xa.py")) && (file_exists("..\apache\conf\python.conf"))) {
		$displayPython = true;
		$a = @ file("$b/python/xa.py");
		$pythonStatus = $a[0];
	}

	if ((file_exists("\modperl\perl.pl")) && (file_exists("..\apache\conf\extra\perl.conf"))) {
		$displayPerl = true;
		$a = @ file("$b/modperl/perl.pl");
		$perlStatus = $a[0];
	}

	if (file_exists("..\MercuryMail\MERCURY.INI")) {
		$displaySmtp = true;
		if (($handle = @ fsockopen($host, 25, $errno, $errstr, $timeout)) == false) {
			$smtpStatus = "NOK";
		} else {
			$smtpStatus = "OK";
		}
		@ fclose($handle);
	}

	if (file_exists("..\FileZillaFTP\FzGSS.dll")) {
		$displayFtp = true;
		if (($handle = @ fsockopen($host, 21, $errno, $errstr, $timeout)) == false) {
			$ftpStatus = "NOK";
		} else {
			$ftpStatus = "OK";
		}
		@ fclose($handle);
	}

	if (file_exists("..\\tomcat\conf\\server.xml")) {
		$displayTomcat = true;
		if (($handle = @ fsockopen($host, 8080, $errno, $errstr, $timeout)) == false) {
			$tomcatStatus = "NOK";
		} else {
			$tomcatStatus = "OK";
		}
		@ fclose($handle);
	}

	if (file_exists("..\PosadisDNS\posadis.exe")) {
		$displayNamed = true;
		if (($handle = @ fsockopen($host, 53, $errno, $errstr, $timeout)) == false) {
			$namedStatus = "NOK";
		} else {
			$namedStatus = "OK";
		}
		@ fclose($handle);
	}

